import React, { Component } from "react"
import AddToDo from "../components/AddToDo"
import ToDoList from "../components/ToDoList"
import ToDoFooter from "../components/ToDoFooter"

class ToDoPage extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      listItems: [
        {
          name: "Homework",
          status: "Completed"
        },
        {
          name: "Dinner",
          status: "Active"
        },
        {
          name: "Coding in React",
          status: ""
        }
      ],
      itemsLeft: 2,
      DisplayList: [
        {
          name: "Homework"
        },
        {
          name: "Dinner"
        },
        {
          name: "Coding in React"
        }
      ]
    }
  }

  filter = (statusType) => {
    let filteredList

    if (statusType !== "All") {
      this.state.listItems.filter((item) => item.status === statusType)
    }
  }

  render() {
    return (
      <div>
        <h1>todos</h1>
        <AddToDo />
        <ToDoList listItems={this.state.listItems} />
        <ToDoFooter itemsLeft={this.state.itemsLeft} />
      </div>
    )
  }
}

export default ToDoPage
